mapper.select:
select id, code, name, common_name commonName, category, specification, place, status, stock from t_drug where 1=1
and name like '%'||#{name}||'%'
order by ${sortField} ${sortOrder}

mapper.update:
update t_drug set
stock = #{stock}
where id = #{id}

mapper.select:
select * from t_log
where drugid = #{drugId}
order by updatedate


        ${sortOrder}


